const thumbnails=document.querySelectorAll(".game__thumbnails img"),slides=document.querySelectorAll(".slide-content");thumbnails.forEach(t=>{t.addEventListener("click",()=>{var e=t.id.slice(5);const s=slides[+parseInt(e)];slides.forEach(e=>{e===s?e.classList.add("slide-content--active"):e.classList.remove("slide-content--active")})})});